INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 1, 'Démontage/remontage ~25 panneaux pertinents', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Démontage d''au moins 25 panneaux pertinents (1er et dernier panneau d''une chaîne, etc.). Remontage sans atteinte à l''intégrité des panneaux ou de leurs supports. (Ref: CDC Girasole §2.2)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 2, 'Conformité DTU 40.35', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Vérification de la conformité aux règles du DTU 40.35 (couvertures par éléments métalliques en feuilles et longues feuilles en zinc). (Ref: DTU 40.35)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 3, 'Qualité montage/serrage SI sur support', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la qualité et conformité du montage et serrage des Structures d''Intégration (SI) sur leur support. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 4, 'Qualité montage/serrage panneaux sur SI', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la qualité et conformité du montage et serrage des panneaux photovoltaïques sur les structures d''intégration. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 5, 'Fixation cheminement câbles (supports, étanchéité)', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la fixation du cheminement des câbles : pertinence des supports de fixation, maintien de l''étanchéité toiture, absence de percement non maîtrisé. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 6, 'Compatibilité/serrage/étanchéité connecteurs MC4', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle des raccordements : compatibilité des connecteurs mâle/femelle (type PVZH202B), serrages corrects, étanchéité assurée, protection contre ruissellement. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 7, 'Qualité cheminement câbles DC dans chemins de câbles', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la qualité du cheminement des câbles DC dans les chemins de câbles : fixation adéquate, rayons de courbure respectés, absence de contraintes mécaniques. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 8, 'Cheminement câbles (pas directement sur couverture)', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Vérification que les câbles ne cheminent PAS directement sur la couverture. Utilisation obligatoire de supports adaptés. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 9, 'Raccordements terres', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la qualité du cheminement et du raccordement des terres : continuité électrique, serrage des connexions, utilisation de cosses adaptées. (Ref: CDC Girasole page 8)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 10, 'Étiquettes réglementaires tension DC', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Présence et conformité des étiquettes réglementaires indiquant la présence de tension DC dangereuse. (Ref: CDC Girasole page 8 + Réglementation)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 11, 'Respect notices montage / Interdiction marcher sur panneaux', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Vérification du respect des notices de montage des panneaux. Contrôle visuel qu''aucune trace de passage sur panneaux n''est visible. INTERDICTION ABSOLUE de marcher sur les panneaux pendant l''audit. (Ref: CDC Girasole §2.2)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 12, 'Présence de film anticondensation ?', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Vérification de la présence et de l''état du film anticondensation sous la toiture (pare-vapeur). Contrôle de son intégrité, de son étanchéité et de sa continuité. (Ref: DTU 40.35 + CDC Girasole)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 13, 'Présence de limaille sous les modules liées au perçage du bac acier pour les supports ? ou points de rouille ou piquage visible ?', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Contrôle de la présence de limaille métallique sous les panneaux (résidus de perçage du bac acier). Vérification de l''absence de points de rouille, de corrosion ou de piquage visible sur la couverture métallique. Ces défauts peuvent compromettre l''étanchéité et la durabilité de la toiture. (Ref: DTU 40.35 + Prévention corrosion)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (6, 14, 'Mesurer la distance Modules > Rives / Modules > Faitage', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Mesure des distances de sécurité entre les modules photovoltaïques et les éléments de toiture : distance modules-rives (bords latéraux de la toiture) et distance modules-faîtage (ligne de crête). Ces distances doivent respecter les normes DTU pour garantir l''étanchéité, la ventilation et la sécurité en cas d''intervention. Mesures en mètres ou centimètres à relever sur site. (Ref: DTU 40.35 + Règles sécurité incendie)');
INSERT INTO checklist_items_toiture (ordre_mission_id, item_numero, libelle, categorie, statut, commentaire) VALUES (8, 1, 'Démontage/remontage ~25 panneaux pertinents', 'AUDIT_TOITURE', 'NON_VERIFIE', 'Démontage d''au moins 25 panneaux pertinents (1er et dernier panneau d''une chaîne, etc.). Remontage sans atteinte à l''intégrité des panneaux ou de leurs supports. (Ref: CDC Girasole §2.2)');
